<div class="form">    <?php    $form = $this->beginWidget('CActiveForm', array(        'id' => 'harga-form',        // Please note: When you enable ajax validation, make sure the corresponding        // controller action is handling ajax validation correctly.        // There is a call to performAjaxValidation() commented in generated controller code.        // See class documentation of CActiveForm for details on this.        'enableAjaxValidation' => false,        'enableClientValidation' => true,        'clientOptions' => array(            'validateOnChange' => false,            'validateOnSubmit' => true        ),        'htmlOptions' => array(            'class' => 'form-horizontal',            'enctype' => 'multipart/form-data',        ),    ));    ?>    <p class="note">Isian dengan tanda <span class="required">*</span> wajib diisi.</p>    <table class="table table-condensed table-bordered">        <tr>            <?php if($harga->isNewRecord): ?>            <th><?php echo $form->labelEx($harga, "HARGA_PRIORITAS") ?></th>            <?php endif ?>            <th><?php echo $form->labelEx($harga, "SATUAN_ID"); ?></th>            <th><?php echo $form->labelEx($harga, "HARGA_NORMAL"); ?></th>            <th><?php echo $form->labelEx($harga, "HARGA_SALE"); ?></th>            <th><?php echo $form->labelEx($harga, "HARGA_PASAR"); ?></th>        </tr>        <tr>            <?php if($harga->isNewRecord): ?>            <td class="th-mini">                <?php echo $form->numberField($harga, "HARGA_PRIORITAS", array('class' => 'input-sm form-control', 'disabled' => $harga->isNewRecord)); ?>                <?php echo $form->error($harga, "HARGA_PRIORITAS"); ?>            </td>            <?php endif ?>            <td>                <?php                echo $form->dropDownList($harga, "SATUAN_ID", $harga->isNewRecord ? Satuan::ListYetBarang($model->BARANG_ID) : Satuan::ListOne($harga->SATUAN_ID), array(                    'prompt' => '-- Pilih Satuan --',                    'class' => 'input-sm form-control'                )) ?>                <?php echo $form->error($harga, "SATUAN_ID"); ?>            </td>            <td class="th-shrink">                <div class="input-group">                    <span class="input-group-addon">Rp.</span>                    <?php echo $form->textField($harga, "HARGA_NORMAL", array('class' => 'input-sm form-control input-shrink')); ?>                </div>                <?php echo $form->error($harga, "HARGA_NORMAL"); ?>            </td>            <td class="th-shrink">                <div class="input-group">                    <span class="input-group-addon">Rp.</span>                    <?php echo $form->textField($harga, "HARGA_SALE", array('class' => 'input-sm form-control input-shrink')); ?>                </div>                <?php echo $form->error($harga, "HARGA_SALE"); ?>            </td>            <td class="th-shrink">                <div class="input-group">                    <span class="input-group-addon">Rp.</span>                    <?php echo $form->textField($harga, "HARGA_PASAR", array('class' => 'input-sm form-control input-shrink')); ?>                </div>                <?php echo $form->error($harga, "HARGA_PASAR"); ?>            </td>            <td></td>        </tr>    </table><br/>    <div class="row">        <?php if ($harga->isNewRecord): ?>            <div class="col-lg-4 col-md-4 col-sm-6 col-xs-12">                <div class="compactRadioGroup">                    <?php echo CHtml::checkBox('ulangi', true) ?>                    <?php echo CHtml::label('Input harga lagi', 'ulangi') ?>                </div>            </div>        <?php endif ?>        <div class="col-lg-4 col-md-4 col-sm-6 col-xs-12">            <?php echo CHtml::htmlButton('<i class="fa fa-save"></i> Simpan', array('class' => 'btn btn-success btn-block', 'type' => 'submit')) ?>        </div>        <?php if ($harga->isNewRecord): ?>            <div class="col-lg-4 col-md-4 col-sm-6 col-xs-12">                <?php echo CHtml::link('<i class="fa fa-check"></i> Selesai', array('view', 'id' => $model->BARANG_ID), array('class' => 'btn btn-primary pull-right')) ?>            </div>        <?php endif ?>    </div>    <?php $this->endWidget(); ?></div>